export const addItemToCart = (cartItems, cartItemToAdd) => {
    const exist = cartItems.find( item => item.id === cartItemToAdd.id );

    if (exist) {
        return cartItems.map( item => {
            item.id === cartItemToAdd.id && {...item, quantity : item.quantity + 1}
        });
    }

    return [...cartItems, {...cartItemToAdd, quantity : 1}]
}
